import logo from "../images/logo.svg";
import { useLocation, Link } from "react-router-dom";
import iconMenu from "../images/icon-menu.svg";
import React from "react";

function Header({signOut, email, width, handleMobileMenu}) {
  const location = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = React.useState(true);
  const [mobileMenuClose, setMobileMenuClose] = React.useState(false);

  function handleCloseMobileMenu(){
    setMobileMenuOpen(false);
  }


  return (
    <header className="header">
      <img className="logo" src={logo} alt="логотип" />
      <div className="header__navigation">
        {location.pathname === "/" && width >= 560 && <p className="header__text">{email}</p>}
        {location.pathname === "/sign-in" && <Link className="header__link opacity-button" to="/sign-up">Регистрация</Link>}
        {location.pathname === "/sign-up" && <Link className="header__link opacity-button" to="/sign-in">Войти</Link>}
        {location.pathname === "/" && width >= 560 && <Link className="header__link opacity-button" to="/sign-in" onClick={signOut}>Выйти</Link>}
        {mobileMenuOpen && width <= 560 && <img onClick={handleMobileMenu} onClic={handleCloseMobileMenu} alt="иконка меню" className="header__icon-menu opacity-button" src={iconMenu} />}
      </div>
    </header>
  );
}

export default Header;
