import React from "react";

import Card from "./Card";
import api from "../utils/api";

function Main(props) {
  const [userName, setUserName] = React.useState();
  const [userDescription, setUserDescription] = React.useState();
  const [userAvatar, setUserAvatar] = React.useState();
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    function handleRequest(data) {
      setUserName(data.name);
      setUserDescription(data.about);
      setUserAvatar(data.avatar);
    }

    api
      .getUserData()
      .then((res) => {
        handleRequest(res);
      })
      .catch((err) => console.log(err));
  }, [userName, userDescription, userAvatar]);

  //   React.useEffect(() => {
  //     function getCards(res){
  //         setCard(res)
  //         console.log(cards)
  //     }

  //     api
  //       .getCards()
  //       .then(res => {
  //           getCards(res);
  //       })
  //   },[cards])

  function getCards() {
    api.getCards().then((res) => {
      setCards(res);
      console.log(cards);
    });
  }

  return (
    <main>
      <section className="profile">
        <div onClick={props.onEditAvatar} className="profile__avatar">
          <img className="profile__edit-view" src={userAvatar} />
        </div>
        <div className="profile__info">
          <h1 className="profile__name">{userName}</h1>
          <button
            onClick={props.onEditProfile}
            className="profile__edit-button opacity-button"
            type="button"
          ></button>
          <p className="profile__occupation">{userDescription}</p>
        </div>
        <button
          onClick={props.onAddPlace}
          className="profile__add-button opacity-button"
          type="button"
        ></button>
      </section>
      <section className="places">
           {cards.map(item => <Card/>)}
      </section>
    </main>
  );
}

export default Main;
