import logo from "../images/logo.svg";
import { useLocation, Link } from "react-router-dom";

function Header() {
  const location = useLocation();

  return (
    <header className="header">
      <img className="logo" src={logo} alt="логотип" />
      {location.pathname === "/sign-in" && <Link className="header__text" to="/sign-up">Регистрация</Link>}
      {location.pathname === "/sign-up" && <p className="header__text">Войти</p>}
      {location.pathname === "/" && <p className="header__text">Выйти</p>}
    </header>
  );
}

export default Header;
