function Main() {
  return (
    <main>
      <section className="profile">
        <div onClick={handleEditAvatarClick} className="profile__avatar">
          <img
            className="profile__edit-view"
            src="<%=require('./images/fonpreload.jpg')%>"
          />
        </div>
        <div className="profile__info">
          <h1 className="profile__name">Жак-Ив Кусто</h1>
          <button
            onClick={handleEditProfileClick}
            className="profile__edit-button opacity-button"
            type="button"
          ></button>
          <p className="profile__occupation">Исследователь океана</p>
        </div>
        <button
          onClick={handleAddPlaceClick}
          className="profile__add-button opacity-button"
          type="button"
        ></button>
      </section>
      <section className="places"></section>
    </main>
  );
}

function handleEditAvatarClick() {
  const popUp = document.querySelector(".popup-avatar");
  popUp.classList.add("popup_active");
}

function handleEditProfileClick() {
  const popUp = document.querySelector(".popup-edit");
  popUp.classList.add("popup_active");
}

function handleAddPlaceClick() {
  const popUp = document.querySelector(".popup-add");
  popUp.classList.add("popup_active");
}

export default Main;
