import register_ok from "../images/register_ok.svg";

function InfoTooltip(props) {
  const textRegisterOk = "Вы успешно зарегистрировались!";
  const textRegisterBad = "Что-то пошло не так! Попробуйте ещё раз.";

  return (
    <div
      className={`popup ${props.name} ${props.isOpen ? "popup_active" : ""}`}
    >
      <div className={"popup__container"}>
        <button
          className={"popup__close opacity-button"}
          type={"button"}
          onClick={props.onClose}
        ></button>
        <img className={"info-tooltip__img"} alt="статус регистрации" src={register_ok}/>
        <p className={"popup__title"}>статус</p>
      </div>
    </div>
  );
}

export default InfoTooltip;
