import logo from "../images/logo.svg";

function Header({location}) {
  return (
    <header className="header">
      <img className="logo" src={logo} alt="логотип" />
      {location.pathname === '/sign-in' ? <p>Регистрация</p> : <p>Войти</p>}
    </header>
  );
}

export default Header;
