function Main() {
  return (
    <main>
      <section className="profile">
        <div className="profile__avatar">
          <img
            className="profile__edit-view"
            src="<%=require('./images/fonpreload.jpg')%>"
          />
        </div>
        <div className="profile__info">
          <h1 className="profile__name">Жак-Ив Кусто</h1>
          <button
            className="profile__edit-button opacity-button"
            type="button"
          ></button>
          <p className="profile__occupation">Исследователь океана</p>
        </div>
        <button
          className="profile__add-button opacity-button"
          type="button"
        ></button>
      </section>
      <section className="places"></section>
    </main>
  );
}

function handleEditAvatarClick(){
    const avatar = document.querySelector('.profile__avatar');
    avatar.addEventListener('click', () => {
      avatar.classList.add('popup_active');
    })
}

export default Main
