import logo from "../images/logo.svg";
import { useLocation } from "react-router-dom";
const locations = useLocation();

function handleLocation(){
  if(locations === '/sign-in'){
    return <p className="header__text">Регистрация</p>
  } else if (locations === '/sign-up') {
    return <p className="header__text">Войти</p>
  }
}

function Header({location}) {
  return (
    <header className="header">
      <img className="logo" src={logo} alt="логотип" />
      {/* {location.pathname === '/sign-in' ? <p className="header__text">Регистрация</p> : <p className="header__text">Войти</p>} */}
      {handleLocation}
    </header>
  );
}

export default Header;
