function ImagePopup(props) {
  return (
    <div className={`popup popup_big-img ${props.card ? 'popup_active' : ''}`}>
      <div className="popup__big-img-container">
        <button className="popup__close opacity-button" type="button" onClick={props.onClose}></button>
        <img
          className="popup__big-img"
          src="https://pictures.s3.yandex.net/frontend-developer/cards-compressed/arkhyz.jpg"
          alt=""
        />
        <h2 className="popup__big-img-title"></h2>
      </div>
    </div>
  );
}

export default ImagePopup;
